        <TopPanel/>
        <Logo/>
        <div className="navigation">
          <div className="nav-icon" tabIndex="0">
            <div></div>
          </div>
          <Navbar/>
        </div>
      </HeaderWrapper>
      <main className="hp-content">
        <Outlet/>
      </main>
      <Footer/>
    </>
  )
}

export default Layout;

