sourcesContent:
import React, { useEffect, useState } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';
import { DefaultEditor } from 'react-simple-wysiwyg';
import {Link} from 'react-router-dom';

const WS_URL = 'ws://localhost:5000';

function isUserEvent(message) {
    let evt = JSON.parse(message.data);
    return evt.type === 'userevent';
}

function isDocumentEvent(message) {
    let evt = JSON.parse(message.data);
    return evt.type === 'contentchange';
}


function LoginSection({ onLogin }) {
    const [username, setUsername] = useState('');

    useWebSocket(WS_URL, {
        share: true,
        filter: () => false
    });
    function logInUser() {
        if(!username.trim()) {
            return;
        }
        onLogin && onLogin(username);
    }

    return (
        <div className="account">
            <div className="account__wrapper">
                <div className="account__card">
                    <div className="account__profile">
                        <p className="account__name">Hello, user!</p>
                        <p className="account__sub">Join to edit the document</p>
                    </div>
                    <input name="username" onInput={(e) => setUsername(e.target.value)} className="form-control" />
                    <button
                        type="button"
                        onClick={() => logInUser()}
                        className="btn btn-primary account__btn">Join</button>
                </div>
            </div>
        </div>
    );
}

function History() {
    console.log('history');
    const { lastJsonMessage } = useWebSocket(WS_URL, {
        share: true,
        filter: isUserEvent
    });
    const activities = lastJsonMessage?.data.userActivity || [];
    return (
        <ul>
            {activities.map((activity, index) => <li key={`activity-${index}`}>{activity}</li>)}
        </ul>
    );
}

function Users() {
    const { lastJsonMessage } = useWebSocket(WS_URL, {
        share: true,
        filter: isUserEvent
    });
    const users = Object.values(lastJsonMessage?.data.users || {});
    return users.map(user => (
        <div key={user.username}>
      <span id={user.username} className="userInfo" key={user.username}>
        {/*<Avatar name={user.username} size={40} round="20px"/>*/}
      </span>
            {/*<UncontrolledTooltip placement="top" target={user.username}>*/}
            {/*    {user.username}*/}
            {/*</UncontrolledTooltip>*/}
        </div>
    ));
}

function EditorSection() {
    return (
        <div className="main-content">
            <div className="document-holder">
                <div className="currentusers">
                    <Users/>
                </div>
                <Document/>
            </div>
            <div className="history-holder">
                <History/>
            </div>
        </div>
    );
}

function Document() {
    const { lastJsonMessage, sendJsonMessage } = useWebSocket(WS_URL, {
        share: true,
        filter: isDocumentEvent
    });

    let html = lastJsonMessage?.data.editorContent || '';
    console.log(lastJsonMessage)

    function handleHtmlChange(e) {
        console.log('handleHtmlChange')
        console.log(e.target.value)
        sendJsonMessage({
            type: 'contentchange',
            content: e.target.value
        });
    }

    return (
        <DefaultEditor value={html} onChange={handleHtmlChange} />
    );
}


const WebSocketTrain = () => {
    const [username, setUsername] = useState('');
    const { sendJsonMessage, readyState } = useWebSocket(WS_URL, {
        onOpen: () => {
            console.log('WebSocket connection established.');
        },
        share: true,
        filter: () => false,
        retryOnError: true,
        shouldReconnect: () => true
    });

    useEffect(() => {
        if(username && readyState === ReadyState.OPEN) {
            sendJsonMessage({
                username,
                type: 'userevent'
            });
        }
    }, [username, sendJsonMessage, readyState]);

    return (
        <>
            <div>
                <Link href="/client/src/pages">Real-time document editor</Link>
            </div>
            <div className="container-fluid">
                {username ? <EditorSection/>
                    : <LoginSection onLogin={setUsername}/> }
            </div>
        </>
    );

}
export default WebSocketTrain;

