import React, {Suspense, useEffect} from 'react';
import axios, {request} from 'axios';
import { Await, defer, Link, useLoaderData } from 'react-router-dom';
import RecipeList from '../components/RecipeList';
import {useHttp} from '../hooks/http.hook';
const wrapPromise = promise => {
    let status = "pending";
    let result = "";
    let suspender = promise.then(
        r => {
            status = "success";
            result = r;
        },
        e => {
            status = "error";
            result = e;
        }
    );

    return {
        read() {
            if (status === "pending") {
                throw suspender;
            } else if (status === "error") {
                throw result;
            }

            return result;
        }
    };
};
const getData = async () => {
    const res = await fetch('http://localhost:3009/api/recipe/all');
    return await res.json();
}
const createResource = () => {
    return {
        recipes: wrapPromise(getData())
    };
};

const RecipesPage = () => {
  const resource = createResource();
    // console.log(recipes)

  return (
    <>
      <div className="recipes-top-panel">
        <div className="filters">Filters will be placed here</div>
        <Link className="add-recipe-btn" to="/create-recipe">Add Recipe</Link>
      </div>
      <div className="recipes">
        <Suspense fallback={<h1>Loading ...</h1>}>
          <RecipeList resource={resource} />}
        </Suspense>
      </div>
    </>
  );
};

const getRecipes = async() => {
  const response = await fetch('/api/recipe/all');
    const data = await response.json();
  return data;
}

const recipesLoader = async() => {
  return defer({
    recipes: getRecipes()
  })
}

export { RecipesPage, recipesLoader }

