import express from "express"
import http from 'http'
const mongoose = require('mongoose')
const bodyParser = require('body-parser')
const cookieParser = require('cookie-parser')
import config from 'config'
import router from './routes'
import ReactDOMServer from 'react-dom/server';
import { StaticRouter } from 'react-router-dom/server';
import AppSSR from 'client/src/AppSSR';
import { bootstrapScripts, bootstrapCSS } from './react.server'

const app = express();
const port = 3009;

app.use('/images', express.static('images'))
app.use(bodyParser.urlencoded({ extended: false }))
app.use(bodyParser.json())
app.use(cookieParser())
app.use('/api', router);
// app.use('/t', require('./routes/redirect.route'))

const staticFiles = "../client/build/static";
const publicFiles = "../../build/static";

app.use("/../client/build/static", express.static(staticFiles));
app.use("/client/public", express.static(publicFiles));
app.use("/", express.static(__dirname + "/client/public/images"));

// app.use(
//     "/images/",
//     express.static(__dirname + "/server/images")
// );

app.get("*", (req, res) => {
    res.socket.on("error", (error) => console.log("Fatal error occured", error));
    let didError = false;
    const stream = ReactDOMServer.renderToPipeableStream(
        <StaticRouter location={req.url}>
            <AppSSR bootStrapCSS={bootstrapCSS} />
        </StaticRouter>,
        {
            bootstrapScripts,
            onShellReady: () => {
                res.statusCode = didError ? 500 : 200;
                res.setHeader("Content-type", "text/html");
                stream.pipe(res);
            },
            onError: (error) => {
                didError = true;
                console.log("Error", error);
            },
        }
    );
});

async function start() {
    try {
        await mongoose.connect(config.get('mongoUri'), {
            useNewUrlParser: true,
            useUnifiedTopology: true

        });

        const server = http.createServer(app)
        server.listen(port, () => {
            console.log(`Server is running on port ${port}`)
        })
    } catch (e) {
        console.log('Server error', e.message)
        process.exit(1)
    }
}


start()

