import { model, Schema, Types } from 'mongoose';

const schema = new Schema({
  email: {
    type: String,
    unique: true,
    required: true
  },
  password: {
    type: String,
    required: true
  },
  isActivated: {
    type: Boolean,
    default: false
  },
  phone: {
    type: Number,
    required: false
  },
  city: {
    type: String,
    required: false
  },
  country: {
    type: String,
    required: false
  },
  activationLink: String,
  recipes: [{
    type: Types.ObjectId,
    ref: 'Recipe'
  }]
});

export default model('User', schema);

